        //                     <div className="card">
        //                         <div className="card-body">
        //                             <h5 className="card-title">{Review.name}</h5>
        //                             <p className="card-text">
        //                                 {Review.review}
        //                             </p>
        //                             <p style={{ color: 'green' }}>
        //                                 <span><b>{Review.stars}</b></span>&nbsp;<i className="fas fa-star"></i>
        //                             </p>


        //                             {/* <div>{star.map(() => <i className="fas fa-star" style={{color:'green'}}></i>)}</div> */}




        //                             <button type="button" className="btn btn-primary" onClick={() => (deleteReview(Review._id))}>
        //                                 Delete Review
        //                             </button>
        //                         </div>
        //                     </div>
        //                 </div>
        //             ))}

        //         </div>
        //         <h4><Link to='/review' className='ms-4'>Back</Link></h4>

        //     </div>
        // </div>
        <div class="swiper looolz">
            <div class="swiper-wrapper">
                <div class="swiper-slide">
                    <img class="img-fluid"
                        src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/Saving-web_1_1_1600x.png?v=1667369063" />
                </div>
                <div class="swiper-slide">
                    <img class="img-fluid"
                        src="https://cdn.shopify.com/s/files/1/0057/8938/4802/files/ACC-DESKTOP-Banner_1600x.png?v=1669638411" />
                </div>
            </div>
            <div class="swiper-button-next"></div>
            <div class="swiper-button-prev"></div>
        </div>

    )
}

