import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';

const AllReviews = () => {

    const [review, setReview] = useState([]);

    const fetchReviews = async() => {
        const res = await fetch('http://localhost:5000/review/getall');
        console.log(res.status)

        const data = await res.json();
        console.log(data);
        setReview(data);
    }

    useEffect(() => {
        fetchReviews();
    }, [])



    return (
        <div className='mt-5'>
            <h1 className='text-center my-3'>ALL REVIEWS HERE</h1>
            <div className='container'>

                <div className="bg-danger mb-5 overflow-y-scroll mx-auto" style={{ width: "80vw", height: '40vh' }}>

                    {review.map((Review) => (
                        <div className="col-md-6 mb-3 mx-auto">
                        <div className="card">
                            <div className="card-body">
                                <h5 className="card-title">{Review.name}</h5>
                                <p className="card-text">
                                    {Review.review}
                                </p>
                                <button type="button" className="btn btn-primary">
                                    Button
                                </button>
                            </div>
                        </div>
                    </div>
                    ))}
                    
                </div>
                <Link to='/review' className=''>Back</Link>

            </div>
        </div>

    )
}

export default AllReviews
